class hTitle extends HTMLElement{constructor(){super(),setTimeout(()=>{let e=this.getAttribute("title"),t=this.getAttribute("subtitle"),n=this.getAttribute("level"),c=this.innerHTML,r=this.attachShadow({mode:"open"});fetch("elements/h-title.html").then(e=>e.text()).then(l=>{let s=(new DOMParser).parseFromString(l,"text/html").querySelector("template"),h=s.content.querySelector("h1");console.log(titleType(n));let a=titleType(n);a.innerHTML=e,h.replaceWith(a),s.content.querySelector("p.subtitle").innerHTML=t,s.content.querySelector("div.emphasis").innerHTML=c,r.appendChild(s.content.cloneNode(!0))})})}}function titleType(e){switch(e){case"h1":case null:case"1":return document.createElement("h1");case"h2":case"2":return document.createElement("h2");case"h3":case"3":return document.createElement("h3");case"h4":case"4":return document.createElement("h4");case"h5":case"5":return document.createElement("h5");case"h6":case"6":return document.createElement("h6");default:return document.createElement("h1")}}customElements.define("h-title",hTitle);