class imgFigure extends HTMLElement{constructor(){super(),setTimeout(()=>{let e=this.getAttribute("src"),t=this.getAttribute("title"),r=this.getAttribute("author"),o=this.getAttribute("link"),n=this.getAttribute("source"),i=this.getAttribute("source-link"),c=this.getAttribute("license"),u=this.innerHTML,l=this.attachShadow({mode:"open"});fetch("elements/img-figure.html").then(e=>e.text()).then(s=>{let a=(new DOMParser).parseFromString(s,"text/html").querySelector("template");a.content.querySelector("img").src=e,null===t?a.content.querySelector("header").remove:a.content.querySelector("header").innerHTML=t,0===u.length?a.content.querySelector("figcaption div.caption").remove:a.content.querySelector("figcaption div.caption").innerHTML=u,null===r?a.content.querySelector("footer a.author").remove:(a.content.querySelector("footer a.author").innerHTML=r,null!==o&&a.content.querySelector("footer a.author").setAttribute("href",o)),null===n?a.content.querySelector("footer a.source").remove:(a.content.querySelector("footer a.source").innerHTML=n,null!==i&&a.content.querySelector("footer a.source").setAttribute("href",i)),null===c?a.content.querySelector("footer a.license").remove:a.content.querySelector("footer a.license").innerHTML=c,l.appendChild(a.content.cloneNode(!0))})})}}customElements.define("img-figure",imgFigure);